sys_script_include.script:
var legalholdUtils = Class.create();
legalholdUtils.prototype = {
    initialize: function() {},

    getRecordsOfTable: function(table_name) {
        var array = [];
        var gr_records = new GlideRecord(table_name);
        gr_records.query();
        while (gr_records.next()) {
            array.push(gr_records.sys_id + "");
        }
        return array.toString();
    },

    getGroupsForLegalHoldRequest: function() {

		var legal_hold_request_resolver = "21a303eb979c311097e0f2a3f153afce";
		var legal_hold_request_user = "2be34f6b979c311097e0f2a3f153af22";
        var array = [];
        var gr_group = new GlideRecord('sys_group_has_role');
        gr_group.addEncodedQuery("role="+legal_hold_request_resolver+"^ORrole="+legal_hold_request_user);
        gr_group.query();
        while (gr_group.next()) {
            array.push(gr_group.group.sys_id + "");
        }
        return array.toString();

    },

    type: 'legalholdUtils'
};
